import { useState } from "react";
import AudioRecord from "./AudioRecord";
import Script from "./Script";
import styled from "styled-components";

const StyledTrain = styled.div`
  position: relative;
  .stage {
    position: absolute;
    left: 50%;
    transform: translateX(-50%);
    img {
      width: 223.68px;
      height: 46.6px;
    }
    img:active {
      transform: scale(0.9); /* 버튼을 작게 축소하는 효과를 줍니다. */
    }
  }
`;

const Train = () => {
  const [recordedAudios, setRecordedAudios] = useState<File[]>([]);
  const [stage, setStage] = useState(0);

  const handleNextClick = () => {
    if (stage < 9) {
      setStage(stage + 1);
    } else if (stage === 9) {
      console.log("Submitting recorded audios to the server:", recordedAudios);
    }
  };

  return (
    <StyledTrain>
      <Script stage={stage} />
      <AudioRecord setRecordedAudios={setRecordedAudios} />
      {stage < 9 ? (
        <div className="stage" onClick={handleNextClick}>
          <img src="/icon/next.png" alt="next"></img>
        </div>
      ) : (
        <div className="stage">
          <img src="/icon/submit.png" alt="next"></img>
        </div>
      )}
    </StyledTrain>
  );
};

export default Train;
