import styled from "styled-components";
import Wrapper from "../common/Wrapper";
import { Link } from "react-router-dom";
const StyledAuth = styled.div`
  position: relative;
  height: 600px;
  .logo {
    position: absolute;
    left: 50%;
    top: 30%;
    transform: translateX(-50%);
    flex: 1;
    font-size: 2rem;
    font-weight: bold;
    align-items: center;
  }
  .logo img {
    width: 313.52px;
    height: 58.98px;
  }
  .login{
    position: absolute;
    left: 50%;
    top: 40%
    transform: translateX(-50%);
  }
`;

const AuthForm = () => {
  return (
    <>
      <Wrapper>
        <StyledAuth>
          <div className="logo">
            <Link to={"/"}>
              <img src="/icon/WorldTone.png" alt="logo"></img>
            </Link>
          </div>
          <div className="login">
            <div className="input">
              <input className="field" type="text" placeholder="id"></input>
            </div>
            <div className="input">
              <input
                className="field"
                type="password"
                placeholder="password"
              ></input>
            </div>
          </div>
        </StyledAuth>
      </Wrapper>
    </>
  );
};

export default AuthForm;
