import axios from "axios";
const api = axios.create({
  baseURL: "http://localhost:8080",
});

export const train_voice = (data: FormData, userId: number) => {
  api
    .post("/train")
    .then((res) => {
      alert(
        "녹음한 음성 파일을 전송을 완료했습니다. 음성 학습은 1~2분 소요될 예정입니다."
      );
    })
    .catch((error) => {
      console.error("Error : ", error);
    });
};
