import { AudioRecorder, useAudioRecorder } from "react-audio-voice-recorder";
import styled from "styled-components";

const StyledAudio = styled.div`
  position: relative;
  height: 150px;
  .record {
    position: absolute;
    left: 50%;
    top: 50%;
    transform: translateX(-50%) translateY(-50%);
    .audio-recorder {
      width: 200px;
      height: 90px;
      img {
        position: absolute;
        left: 50%;
        transform: translateX(-50%);
        width: 70px;
        height: 70px;
        padding: 10px;
      }
    }
  }
`;
const AudioRecord = ({
  setRecordedAudios,
  stage,
}: {
  setRecordedAudios: React.Dispatch<React.SetStateAction<File[]>>;
  stage: number;
}) => {
  const recorderControls = useAudioRecorder();
  const addAudioElement = (blob: Blob) => {
    const url = URL.createObjectURL(blob);
    const audio = document.createElement("audio");
    audio.src = url;
    audio.controls = true;
    document.body.appendChild(audio);

    const fileName = "recorded_audio" + ".wav";
    const audioFile = blobToFile({ blob, fileName });
    setRecordedAudios((prevAudios) => [...prevAudios, audioFile]);
  };

  const blobToFile = ({
    blob,
    fileName,
  }: {
    blob: Blob;
    fileName: string;
  }): File => {
    const file = new File([blob], fileName, {
      type: blob.type,
      lastModified: Date.now(),
    });
    return file;
  };
  return (
    <StyledAudio>
      <div className="record">
        <AudioRecorder
          onRecordingComplete={(blob) => addAudioElement(blob)}
          recorderControls={recorderControls}
        />
      </div>
      {/* <button onClick={recorderControls.stopRecording}>Stop recording</button> */}
    </StyledAudio>
  );
};

export default AudioRecord;
